    setFilters(new Array(g, m, i, v));
  };

  return (
    <div>
      <Header></Header>
      <ClothesProvider>
        <Form onAddItem={addClothesHandler}></Form>
        <br></br>
        {filteredExpenses.map(expense => (
          <Products
            key={expense.id}
            gender={expense.gender}
            mark={expense.mark}
            item={expense.item}
            value={expense.value}
          />
        ))}
      </ClothesProvider>
    </div>
  );
}

export default App;
