import classes from './Form.module.css';
import React, { useState, useContext, useEffect } from 'react';
import FilterItem from './FilterItem';
import ClothesContext from '../store/clothes-context';
import Products from './Products';

const DUMMY_DATA = [
  {
    id: '1',
    gender: 'Male',
    mark: 'Nike',
    item: 'Sneakers',
    value: 94.12,
  },
  { id: '2', gender: 'Female', mark: 'Adidas', item: 'Socks', value: 5.49 },
  {
    id: '3',
    gender: 'Male',
    mark: 'Puma',
    item: 'T-Shirt',
    value: 34.67,
  },
  {
    id: '4',
    gender: 'Female',
    mark: 'Reebok',
    item: 'Trousers',
    value: 45.0,
  },
  {
    id: '5',
    gender: 'Female',
    mark: 'Puma',
    item: 'T-Shirt',
    value: 124.67,
  },
  {
    id: '6',
    gender: 'Male',
    mark: 'Reebok',
    item: 'Jacket',
    value: 55.0,
  },
  {
    id: '7',
    gender: 'Female',
    mark: 'Puma',
    item: 'Dress',
    value: 64.67,
  },
  {
    id: '8',
    gender: 'Male',
    mark: 'Reebok',
    item: 'Jeans',
    value: 75.0,
  },
  {
    id: '9',
    gender: 'Female',
    mark: 'Puma',
    item: 'Jacket',
    value: 64.67,
  },
  {
    id: '10',
    gender: 'Male',
    mark: 'Reebok',
    item: 'T-Shirt',
    value: 25.0,
  },
  {
    id: '11',
    gender: 'Female',
    mark: 'Puma',
    item: 'Sneakers',
    value: 64.67,
  },
  {
    id: '12',
    gender: 'Male',
    mark: 'Reebok',
    item: 'Trousers',
    value: 45.0,
  },
];

const Form = props => {
  const [filteredExpenses, setFilteredExpenses] = useState([]);
  const { filters, setFilter } = useContext(ClothesContext);

  const formSubmitHandler = event => {
    event.preventDefault();
    //props.onAddItem(genderChosen, markChosen, itemChosen, valueChosen);
  };

  useEffect(() => {
    console.log(filters);

    let expenses = DUMMY_DATA;

    if (filters[0] !== '')
      expenses = expenses.filter(expense => expense.gender === filters[0]);

    if (filters[1] !== '')
      expenses = expenses.filter(expense => expense.mark === filters[1]);

    if (filters[2] !== '')
      expenses = expenses.filter(expense => expense.item === filters[2]);

    if (filters[3] !== '')
      expenses = expenses.filter(
        expense =>
          expense.value >= Number(filters[3].split('-')[0]) &&
          expense.value <= Number(filters[3].split('-')[1])
      );

    setFilteredExpenses(expenses);
  }, [filters]);

  const changeHandlerMark = event => {
    setFilter(1, event.target.value);
  }

  const changeHandlerItem = event => {
    setFilter(2, event.target.value);
  }
  
  const changeHandlerValue = event => {
    setFilter(3, event.target.value);
  }

  return (
    <div className={classes.form}>
      <form onSubmit={formSubmitHandler} className={classes['form-items']}>
        <div className={classes.buttons}>
          <button id="maleFilter" onClick={() => setFilter(0, 'Male')}>
            <img src="m.png" alt="" width="19" height="19" />
          </button>
          <button id="femaleFilter" onClick={() => setFilter(0, 'Female')}>
            <img src="f.png" alt="" width="15" height="15" />
          </button>
        </div>
        <select onChange={changeHandlerMark}>
          <option value="Nike">Nike</option>
          <option value="Reebok">Reebok</option>
          <option value="Adidas">Adidas</option>
          <option value="Puma">Puma</option>
        </select>
        <select onChange={changeHandlerItem}>
          <option value="T-Shirt">T-Shirt</option>
          <option value="Trousers">Trousers</option>
          <option value="Jeans">Jeans</option>
          <option value="Sneakers">Sneakers</option>
          <option value="Dress">Dress</option>
          <option value="Jacket">Jacket</option>
        </select>
        <select onChange={changeHandlerValue}>
          <option value="50-100">50 KM - 100 KM</option>
          <option value="100-200">100 KM - 200 KM</option>
          <option value="200-300">200 KM - 300 KM</option>
          <option value="300-400">300 KM - 400 KM</option>
        </select>
        <div>
          <button className={classes.searchButton} type="submit">
            Search
          </button>
        </div>

        <FilterItem index="0" value={filters[0]} />
        <FilterItem index="1" value={filters[1]} />
        <FilterItem index="2" value={filters[2]} />
        <FilterItem index="3" value={filters[3]} />
      </form>
      <br /><br /><br />
      {filteredExpenses.map(expense => (
          <Products
            key={expense.id}
            gender={expense.gender}
            mark={expense.mark}
            item={expense.item}
            value={expense.value}
          />
        ))}
    </div>
  );
};

export default Form;
