import React, { useState, useEffect, useContext } from 'react';
import ClothesContext from '../store/clothes-context';
import classes from './FilterItem.module.css';

const FilterItem = props => {
  const [index] = useState(props.index);
  const [filterValue, setFilterValue] = useState(props.value);
  const { removeFilter } = useContext(ClothesContext);

  useEffect(() => {
    setFilterValue(props.value);
  }, [props]);

  if (filterValue === '') return <div></div>;

  return (
    <div className={classes.filterItems}>
      <p>{filterValue}</p>
      <button onClick={() => removeFilter(index)}>
        <img src="../image/x.png" alt="" />
      </button>
    </div>
  );
};

export default FilterItem;
