import classes from './Form.module.css';
import React, { useState } from 'react';

const Form = () => {
    const [genderChosen, setGenderChosen] = useState('');
    const [markChosen, setMarkChosen] = useState('');
    const [itemChosen, setItemChosen] = useState('');
    const [valueChosen, setValueChosen] = useState('');

    const changeHandlerGend = (event) => {
        console.log(event.target.value);
        
    };
    const changeHandlerMark = (event) => {
        console.log(event.target.value);
    };
    const changeHandlerItem = (event) => {
        console.log(event.target.value);
    };
    const changeHandlerValue = (event) => {
        console.log(event.target.value);
    };

    const formSubmitHandler = event => {
        event.preventDefault();
    }
  return (
    <div className={classes.form}>
      <form onSubmit={formSubmitHandler} className={classes['form-items']}>
        <select  onClick={changeHandlerGend} className={classes.gender}>
          <option value="mr">Mr.</option>
          <option value="ms">Ms.</option>
        </select>
        <select onClick={changeHandlerMark}>
          <option value="mr">Nike</option>
          <option value="ms">Zara</option>
          <option value="ms">Adidas</option>
          <option value="ms">Puma</option>
        </select>
        <select onClick={changeHandlerItem}>
          <option value="mr">T-Shirt</option>
          <option value="ms">Trousers</option>
          <option value="ms">Jeans</option>
          <option value="ms">Sneakers</option>
          <option value="ms">Dress</option>
          <option value="ms">Jacket</option>
        </select>
        <select onClick={changeHandlerValue}>
          <option value="mr">50 KM - 100 KM</option>
          <option value="ms">100 KM - 200 KM</option>
          <option value="ms">200 KM - 300 KM</option>
          <option value="ms">300 KM - 400 KM</option>
        </select>
        <div>
        <button type="submit" className={classes.button}>
          Search
        </button>
        </div>
      </form>
    </div>
  );
};

export default Form;
