import Products from './components/Products';
import Header from './components/Header';
import Form from './components/Form';
import React, { useState, useEffect } from 'react';

const DUMMY_DATA = [
  {
    id: '1',
    gender: 'Male',
    mark: 'Nike',
    item: 'Sneakers',
    value: 94.12,
  },
  { id: '2', gender: 'Female', mark: 'Adidas', item: 'Socks', value: 5.49 },
  {
    id: '3',
    gender: 'Male',
    mark: 'Puma',
    item: 'T-Shirt',
    value: 34.67,
  },
  {
    id: '4',
    gender: 'Female',
    mark: 'Reebok',
    item: 'Trousers',
    value: 45.0,
  },
  {
    id: '5',
    gender: 'Female',
    mark: 'Puma',
    item: 'T-Shirt',
    value: 24.67,
  },
  {
    id: '6',
    gender: 'Male',
    mark: 'Reebok',
    item: 'Jacket',
    value: 55.0,
  },
  {
    id: '7',
    gender: 'Female',
    mark: 'Puma',
    item: 'Dress',
    value: 64.67,
  },
  {
    id: '8',
    gender: 'Male',
    mark: 'Reebok',
    item: 'Jeans',
    value: 75.0,
  },
  {
    id: '9',
    gender: 'Female',
    mark: 'Puma',
    item: 'Jacket',
    value: 64.67,
  },
  {
    id: '10',
    gender: 'Male',
    mark: 'Reebok',
    item: 'T-Shirt',
    value: 25.0,
  },
  {
    id: '11',
    gender: 'Female',
    mark: 'Puma',
    item: 'Sneakers',
    value: 64.67,
  },
  {
    id: '12',
    gender: 'Male',
    mark: 'Reebok',
    item: 'Trousers',
    value: 45.0,
  },
];

function App() {
  const [filteredExpenses, setFilteredExpenses] = useState(DUMMY_DATA);
  const [filters, setFilters] = useState([]);

  const addClothesHandler = (g, m, i, v) => {
    setFilters(new Array(g, m, i, v));
  };

  useEffect(() => {
    console.log(filters);
    let expenses = DUMMY_DATA;

    if (filters[0] !== '')
      expenses = expenses.filter(expense => expense.gender === filters[0]);

    if (filters[1] !== '')
      expenses = expenses.filter(expense => expense.mark === filters[1]);

    if (filters[2] !== '')
      expenses = expenses.filter(expense => expense.item === filters[2]);

    setFilteredExpenses(expenses);
  }, [filters]);

  return (
    <div>
      <Form onAddItem={addClothesHandler}></Form>
      <Header></Header>

      <br></br>
      {filteredExpenses.map(expense => (
        <Products
          key={expense.id}
          gender={expense.gender}
          mark={expense.mark}
          item={expense.item}
          value={expense.value}
        />
      ))}
    </div>
  );
}

export default App;
