import classes from './Form.module.css';
import React, { useState, useContext } from 'react';
import FilterItems from './FilterItems';
import ClothesContext from '../store/clothes-context';

const Form = props => {
  const [filteredExpenses, setFilteredExpenses] = useState(DUMMY_DATA);
  const { filters } = useContext(ClothesContext);

  const formSubmitHandler = event => {
    event.preventDefault();
    props.onAddItem(genderChosen, markChosen, itemChosen, valueChosen);
  };

  return (
    <div className={classes.form}>
      <form onSubmit={formSubmitHandler} className={classes['form-items']}>
        <div className={classes.buttons}>
          <button id="maleFilter" onClick={() => changeHandlerGend('Male')}>
            <img src="m.png" alt="" width="19" height="19" />
          </button>
          <button id="femaleFilter" onClick={() => changeHandlerGend('Female')}>
            <img src="f.png" alt="" width="15" height="15" />
          </button>
        </div>

        <select onChange={changeHandlerMark}>
          <option value="Nike">Nike</option>
          <option value="Reebok">Reebok</option>
          <option value="Adidas">Adidas</option>
          <option value="Puma">Puma</option>
        </select>
        <select onChange={changeHandlerItem}>
          <option value="T-Shirt">T-Shirt</option>
          <option value="Trousers">Trousers</option>
          <option value="Jeans">Jeans</option>
          <option value="Sneakers">Sneakers</option>
          <option value="Dress">Dress</option>
          <option value="Jacket">Jacket</option>
        </select>
        <select onChange={changeHandlerValue}>
          <option value="50-100">50 KM - 100 KM</option>
          <option value="100-200">100 KM - 200 KM</option>
          <option value="200-300">200 KM - 300 KM</option>
          <option value="300-400">300 KM - 400 KM</option>
        </select>
        <div>
          <button className={classes.searchButton} type="submit">
            Search
          </button>
        </div>

        <FilterItems
          gender={genderChosen}
          mark={markChosen}
          item={itemChosen}
          value={valueChosen}
        ></FilterItems>
      </form>
    </div>
  );
};

export default Form;
