import classes from './Form.module.css';
import React, { useState } from 'react';
import FilterItems from './FilterItems';

const Form = (props) => {
    const [genderChosen, setGenderChosen] = useState('');
    const [markChosen, setMarkChosen] = useState('');
    const [itemChosen, setItemChosen] = useState('');
    const [valueChosen, setValueChosen] = useState('');

    const changeHandlerGend = (event) => {
        setGenderChosen(event.target.value);
        
    };
    const changeHandlerMark = (event) => {

        setMarkChosen(event.target.value);
    };
    const changeHandlerItem = (event) => {
        setItemChosen(event.target.value);
    };
    const changeHandlerValue = (event) => {
        setValueChosen(event.target.value);
    };

    const formSubmitHandler = event => {
        event.preventDefault();
        props.onAddItem(genderChosen, markChosen, itemChosen, valueChosen);
    }
  return (
    <div className={classes.form}>
      <form onSubmit={formSubmitHandler} className={classes['form-items']}>
        <select  onChange={changeHandlerGend} className={classes.gender}>
          <option value="Male">Mr.</option>
          <option value="Female">Ms.</option>
        </select>
        <select onChange={changeHandlerMark}>
          <option value="Nike">Nike</option>
          <option value="Reebok">Reebok</option>
          <option value="Adidas">Adidas</option>
          <option value="Puma">Puma</option>
        </select>
        <select onChange={changeHandlerItem}>
          <option value="T-Shirt">T-Shirt</option>
          <option value="Trousers">Trousers</option>
          <option value="Jeans">Jeans</option>
          <option value="Sneakers">Sneakers</option>
          <option value="Dress">Dress</option>
          <option value="Jacket">Jacket</option>
        </select>
        <select onChange={changeHandlerValue}>
          <option value="50 KM - 100 KM">50 KM - 100 KM</option>
          <option value="100 KM - 200 KM">100 KM - 200 KM</option>
          <option value="200 KM - 300 KM">200 KM - 300 KM</option>
          <option value="300 KM - 400 KM">300 KM - 400 KM</option>
        </select>
        <div>
        <button type="submit" className={classes.button}>
          Search
        </button>
        </div>
        <FilterItems gender={genderChosen} mark={markChosen} item={itemChosen} value={valueChosen}></FilterItems>
      </form>
      <div>
      
      </div>
    </div>
  );
};

export default Form;
