import Header from './components/Header';
import Form from './components/Form';
import React, { useState } from 'react';

function App() {
  const expenses = [
    {
      id: 'e1',
      title: 'Sneakers',
      amount: 94.12,

    },
    { id: 'e2', title: 'Socks', amount: 799.49,  },
    {
      id: 'e3',
      title: 'T-shirt',
      amount: 294.67,

    },
    {
      id: 'e4',
      title: 'Trousers',
      amount: 450,

    },
  ];
  const [filters, setFilters] = useState('');

  const addClothesHandler = (g, b, i, v) => {
    console.log(g + b + i + v);
    setFilters([g, b, i, v]);
  }


  return (
    <div>
      <Form onAddItem={addClothesHandler}></Form>
      <Header></Header>  

      <h2> DATA from API</h2>
      <Products
       title={expenses[0].title}
       amount={expenses[0].amount}
       >

        </Products>
      <Products
      title={expenses[1].title}
      amount={expenses[1].amount}
     >

        </Products>
        <Products
        title={expenses[2].title}
        amount={expenses[2].amount}
        >

        </Products>
      <Products
       title={expenses[3].title}
       amount={expenses[3].amount}
     >

        </Products>
    </div>
  );
}

export default App;
